BEGIN

DECLARE @UtcNow AS DATETIME2(7) = GETUTCDATE()
DECLARE @CurrentDay INT = DATEPART(DD, @utcnow)
DECLARE @StartOfMonth AS DATETIME2(7) =  DATEADD(month, DATEDIFF(month, 0, @UtcNow), 0)
DECLARE @firstDayOfMonth AS DATETIME2(7) =  DATEFROMPARTS(YEAR(@UtcNow),MONTH(@UtcNow),1)
DECLARE @formattedDate AS VARCHAR(10) = CONVERT(VARCHAR(10), @firstDayOfMonth, 101)
DECLARE @DaysInMonth AS INT = DAY(EOMONTH(@UtcNow))
DECLARE @TotalMetricDays AS INT = DATEDIFF(DD, @firstDayOfMonth, @UtcNow)
DECLARE @RequiresCalculation AS BIT = CASE WHEN (@DaysInMonth - @CurrentDay) > 0 THEN 1 ELSE 0 END

MERGE INTO [dbo].[O365PreviewOneDriveDates] AS TARGET USING (VALUES
	(@formattedDate, @firstDayOfMonth, @UtcNow, @TotalMetricDays, @RequiresCalculation)
) AS Source ([FormattedDate], [DTSTART], [DTEND], [TotalMetricDays], [RequiresCalculation])
ON (Target.[FormattedDate] = Source.[FormattedDate])
WHEN MATCHED THEN UPDATE SET
	[DTEND] = Source.[DTEND],
	[TotalMetricDays] = Source.[TotalMetricDays],
	[RequiresCalculation] = Source.[RequiresCalculation]
WHEN NOT MATCHED BY TARGET THEN
	INSERT ([FormattedDate], [DTSTART], [DTEND], [TotalMetricDays], [RequiresCalculation])
	VALUES (Source.[FormattedDate], Source.[DTSTART], Source.[DTEND], Source.[TotalMetricDays], Source.[RequiresCalculation]);


END
